import { useParams } from "react-router-dom";
import { Container, Col, Row } from "react-bootstrap";
import React from "react";
import { connect } from "react-redux";
import { Button, Popover, OverlayTrigger } from "react-bootstrap";
import "./Blog.css";
const Blog1 = ({ brand }) => {
  const { id } = useParams();

  let [product] = brand.filter((brand) => brand.id === id);

  const popover = (
    <Popover id="popover-basic">
      <Popover.Header as="h3">REACH US ON </Popover.Header>
      <Popover.Body>
        <h5>0550300103</h5>
        <h5>0572211378</h5>
        <p>EMAIL : antwi2k@gmail.com</p>
      </Popover.Body>
    </Popover>
  );
  const Example = () => (
    <OverlayTrigger trigger="click" placement="right" overlay={popover}>
      <Button variant="warning">Buy me</Button>
    </OverlayTrigger>
  );
  return (
    <div className="bloglist d-grid gap-2">
      <Container>
        <Row>
          <Col sm={4} md={4} className="items-img">
            <img
              src={product.imgUrl}
              alt="pic"
              className="item img-responsive"
            />
          </Col>
          <Col sm={8} md={8}>
            <h1 className="items-name">{product.name}</h1>
            <p>{product.price}</p>
            <Example />
          </Col>
        </Row>
      </Container>
    </div>
  );
};
const mapStateToprops = (state) => {
  return { brand: state.brand };
};

export default connect(mapStateToprops)(Blog1);
