import React, { Component } from "react";
import { Navbar, FormControl, Form, Button } from "react-bootstrap";
import {
  Dropdown,
  DropdownButton,
  ButtonGroup,
  Container,
  Row,
  Col,
} from "react-bootstrap";
import "./Nav.css";
import { connect } from "react-redux";

class Nav extends Component {
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  handleSubmit = (e) => {
    e.preventDefault();
  };
  render() {
    return (
      <>
        <Container fluid className=" fixed-top">
          <Row>
            <Col sm={6} md={12}>
              <Navbar className="Navbar">
                <h3
                  style={{
                    marginLeft: `10%`,
                    fontSize: `40px`,
                    fontWeight: `bolder`,
                    fontStyle: `italic`,
                  }}
                >
                  Blue
                  <span style={{ color: `orange`, fontFamily: `sans-serif` }}>
                    Let
                  </span>
                </h3>
                <Form className="d-flex form" onSubmit={this.handleSubmit}>
                  <FormControl
                    type="search"
                    name="input"
                    onChange={this.handleChange}
                    value={this.state}
                    placeholder="Search product, brands and categories"
                    className="mr-2 input"
                    aria-label="Search"
                  />
                  <Button variant="warning" size="sm">
                    Search
                  </Button>
                </Form>
                <i class="fa fa-user" aria-hidden="true"></i>
                {["Account"].map((variant) => (
                  <DropdownButton
                    as={ButtonGroup}
                    key={variant}
                    id={`dropdown-variants-${variant}`}
                    variant={variant.toLowerCase()}
                    title={variant}
                    className="drop"
                  >
                    <Dropdown.Item eventKey="1" className="login">
                      Login
                    </Dropdown.Item>
                    <Dropdown.Item eventKey="2">My Account</Dropdown.Item>
                  </DropdownButton>
                ))}
              </Navbar>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}
const mapStateToProps = (state) => {
  return { state: state };
};

export default connect(mapStateToProps)(Nav);
