import { useParams } from "react-router-dom";
import { Container, Col, Row } from "react-bootstrap";
import React from "react";
import { connect } from "react-redux";
import { Button } from "react-bootstrap";

const Blog1 = (props) => {
  const { id } = useParams();

  let [brand] = brand.filter((brand) => brand.id === id);

  return (
    <div className="bloglist d-grid gap-2">
      <Container>
        <Row>
          <Col sm={4} md={4} className="items-img">
            <img
              src={props.brand.imgUrl}
              alt="pic"
              className="item img-responsive"
            />
          </Col>
          <Col sm={8} md={8}>
            <h1 className="items-name">{props.brand.name}</h1>
            <p>{props.brand.price}</p>
            <Button variant="primary" size="lg">
              Buy Me{" "}
            </Button>{" "}
          </Col>
        </Row>
      </Container>
    </div>
  );
};
const mapStateToprops = (state) => {
  return { brand: state.brand };
};

export default connect(mapStateToprops)(Blog1);
