import React from "react";
import { Navbar, FormControl, Form, Button } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";

class Footer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "",
    };
  }
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
      input: "",
    });
  };
  render() {
    return (
      <>
        <Container fluid>
          <Row>
            <Col sm={6} md={12}>
              <Navbar className="footer">
                <h3
                  style={{
                    marginLeft: `10%`,
                    fontSize: `40px`,
                    fontWeight: `bolder`,
                    fontStyle: `italic`,
                  }}
                >
                  Blue
                  <span style={{ color: `orange`, fontFamily: `sans-serif` }}>
                    Let
                  </span>
                </h3>
                <Form className="d-flex form" onSubmit={this.handleSubmit}>
                  <FormControl
                    type="search"
                    name="input"
                    onChange={this.handleChange}
                    value={this.state.input}
                    placeholder="Search product, brands and categories"
                    className="mr-2 input"
                    aria-label="Search"
                  />
                  <Button variant="warning" size="sm">
                    Search
                  </Button>
                </Form>
              </Navbar>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default Footer;
